const fs = require('fs');
const path = require('path');
const nodeUtils = new (require('busybox/utils/node'))();
const through = require('through');

const filePath = nodeUtils.findClosestFile(__dirname, 'assets/files/史蒂夫-乔布斯传.txt');

const reader = fs.createReadStream(filePath);

var ps = through().pause();
reader.pipe(ps);

const slowStream = nodeUtils.slowStream();
ps.pipe(slowStream);

ps.resume();

slowStream.pipe(process.stdout);

